function getActiveServices() {
    global $db;
    $stmt = $db->query("SELECT *, 
        CASE 
        WHEN name = 'Instagram' THEN '#E1306C'
            WHEN name = 'TikTok' THEN '#000000'
            WHEN name = 'Twitter' THEN '#1DA1F2'
            WHEN name = 'YouTube' THEN '#FF0000'
            WHEN name = 'Facebook' THEN '#4267B2'
            WHEN name = 'Spotify' THEN '#1DB954'
            WHEN name = 'Telegram' THEN '#0088cc'
            WHEN name = 'LinkedIn' THEN '#0077B5'
            WHEN name = 'Kick' THEN '#32CD32'
            WHEN name = 'Twitch' THEN '#9146FF'
            WHEN name = 'Trovo' THEN '#0EAB89'
            WHEN name = 'Google' THEN '#4285F4'
            WHEN name = 'App Store' THEN '#007AFF'
            WHEN name = 'SEO' THEN '#FFD700'
            WHEN name = 'DLive' THEN '#FFCC00'
            WHEN name = 'Nonolive' THEN '#FF4500'
            WHEN name = 'Tumblr' THEN '#36465D'
            WHEN name = 'SoundCloud' THEN '#FF8800'
            WHEN name = 'Reddit' THEN '#FF4500'
            WHEN name = 'Pinterest' THEN '#E60023'
            WHEN name = 'Discord' THEN '#5865F2'
            WHEN name = 'Snapchat' THEN '#ffc188'
            WHEN name = 'PUBG' THEN '#FFC107'
            ELSE '#6c757d'
        END as color
        FROM services 
        WHERE is_active = 1 
        ORDER BY id ASC");
    return $stmt->fetchAll();
}

function getFeaturedPackages() {
    global $db;
    $stmt = $db->query("
        SELECT p.*, s.name as service_name, s.icon 
        FROM packages p 
        JOIN services s ON p.service_id = s.id 
        WHERE p.is_featured = 1
    ");
    return $stmt->fetchAll();
}

